import React from 'react'
import { createClient } from "@sanity/client";
import { useState } from 'react';

async function getProducts(){
    const client = createClient({
        projectId: '63xf4beu',
        dataset: 'store',
        apiVersion: "2024-01-19",
        useCdn: true,
    });

    return client.fetch(
        `*[_type == "product"]`
    )
}
  const products = await getProducts();




const SearchBar = () => {

    const [search, setSearch] = useState('')

    function captureChange(event){
        const searchQuery = event.target.value
        if (searchQuery.length >=2){
            setSearch(searchQuery)
            console.log(search);
        }

     }


     const searchItems = products.map((item)=>{

            if (item.name.toLowerCase().includes(search)=== true){
                return <p style={{padding:"10px"}}>{item.name}</p>
            }



     })



return <div className='search-bar'>
    <input type="text" className='search' placeholder='Search...' onChange={captureChange} value={search}></input>
    <div className='search-results'>{searchItems}</div>
</div>
}
export default SearchBar
