import React from 'react'
import { createClient } from "@sanity/client";
import { useState } from 'react';

async function getProducts(){
    const client = createClient({
        projectId: '63xf4beu',
        dataset: 'store',
        apiVersion: "2024-01-19",
        useCdn: true,
    });

    return client.fetch(
        `*[_type == "product"]`
    )
}
  const products = await getProducts();




const SearchBar = () => {

    const [search, setSearch] = useState('goo')

    function captureChange(event){
        const searchQuery = event.target.value
        if (searchQuery.length > 3){
            setSearch(searchQuery)
        }
     }

     const searchResult = products.filter(product => product.name.includes(search) === true)

     console.log(searchResult);

     const searchItems = searchResult.map((item)=>{
        return <p>{item.name}</p>
     })



return <div className='search-bar'>
    <input type="text" className='search' placeholder='Search...' onChange={captureChange}></input>
    <div className='search-results'>{searchItems}</div>
</div>
}
export default SearchBar
